import React from "react";
import "./MoviesCardList.css";
import MoviesForm from '../MoviesForm/MoviesForm';
import one from '../../images/1.png';
import two from '../../images/2.png';
import three from '../../images/3.png';
import four from '../../images/4.png';
import five from '../../images/5.png';
import six from '../../images/6.png';

// На роуте saved-movies будет отображаться конкретный блок с карточками

function MoviesCardList({ saved }) {
  return (
    <>
      {saved ? (
        <section className="moviesgallery">
          <ul className="moviesgallery__wrapper">
          <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq-2'}
            />
            <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq-2'}
            />
            <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq-2'}
            />
          </ul>
        </section>
      ) : (
        <section className="moviesgallery">
          <ul className="moviesgallery__wrapper">
            <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq-2'}
            />
            <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq-2'}
            />
            <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq'}
            />
            <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq'}
            />
            <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq'}
            />
            <MoviesForm
              name='avatar'
              time='1ч 42м'
              link={one}
              btn={'qqq-2'}
            />

          </ul>
          <div className="moviesgallery__wrapper-button">
            <button className="moviesgallery__button-card-more">Ещё</button>
          </div>
        </section>
      )}
    </>
  );
}

export default MoviesCardList;
