import React from "react";
import { Link, Route } from 'react-router-dom';
import logo from '../../images/logo.png'
import './header.css';

function Header({ isLoggedIn, isProfileEmail }) {
    return (
        <header className={`${isLoggedIn ? 'header' : 'header-movies'}`}>
        {/* <header className="header"> */}
        <img aria-label="Логотип Movies" className="header__logo" src={logo} />
            {/* <Route path="/sign-in">
                <Link to='sign-up' className="header__link">Регистрация</Link>
            </Route>
            <Route path="/sign-up">
                <Link to='sign-in' className="header__link">Войти</Link>
            </Route> */}
            <Route exact path="/">
                {/* <Link to='sign-up' className="header__link">Регистрация</Link>
                <Link to='sign-in' className="header__link">Войти</Link> */}
                <div className="header__info">
                <Link to='sign-up' className="header__link">Регистрация</Link>
                <Link to='sign-in' className="header__link">Войти</Link>
                    <p className="header__email">{isProfileEmail}</p>
                    <Link to='sign-in'
                        onClick={() => {
                            onLogout();
                        }}
                        className="header__btn-exit">Выйти</Link>
                </div>
            </Route>
        </header>
    );
}

export default Header;
